import React, { useState } from 'react';

const feeTiers = [
  { id: 'early', label: 'Early bird (≤ June 30, 2025)', amount: 100000, deadline: new Date('2025-06-30T23:59:59') },
  { id: 'late', label: 'Late (July 1 ‑ Aug 31, 2025)', amount: 120000, deadline: new Date('2025-08-31T23:59:59') },
  { id: 'onsite', label: 'Onsite (from Sept 1, 2025)', amount: 150000, deadline: null },
];

function getCurrentFee() {
  const now = new Date();
  if (now <= feeTiers[0].deadline) return feeTiers[0];
  if (now <= feeTiers[1].deadline) return feeTiers[1];
  return feeTiers[2];
}

const Registration = ({ onRegistered }) => {
  const [form, setForm] = useState({ full_name: '', email: '', phone: '', evidence: null });
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const currentFee = getCurrentFee();

  const handleChange = e => {
    const { name, value, files } = e.target;
    setForm(f => ({ ...f, [name]: files ? files[0] : value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setSubmitting(true);
    setError('');
    const data = new FormData();
    data.append('full_name', form.full_name);
    data.append('email', form.email);
    data.append('phone', form.phone);
    data.append('amount', currentFee.amount);
    data.append('payment_type', currentFee.id);
    data.append('evidence', form.evidence);
    try {
      const res = await fetch('/api/register', {
        method: 'POST',
        body: data,
      });
      if (!res.ok) throw new Error('Registration failed');
      onRegistered();
    } catch (err) {
      setError(err.message);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <section className="page active">
      <h2>Payment Details</h2>
      <ul>
        {feeTiers.map(tier => (
          <li key={tier.id} className={`fee-item${tier.id === currentFee.id ? ' highlight' : ''}`}>{tier.label}: ₦{tier.amount.toLocaleString()}</li>
        ))}
      </ul>
      <p><strong>Account:</strong> MDCAN BDM ENUGU | <strong>Bank:</strong> UBA | <strong>No.:</strong> 1027616784</p>
      <form onSubmit={handleSubmit}>
        <label>Full Name:<input name="full_name" value={form.full_name} onChange={handleChange} required /></label>
        <label>Email:<input name="email" type="email" value={form.email} onChange={handleChange} required /></label>
        <label>Phone:<input name="phone" value={form.phone} onChange={handleChange} required /></label>
        <label>Upload Evidence of Payment:<input type="file" name="evidence" accept="image/*,application/pdf" onChange={handleChange} required /></label>
        <button className="btn btn-primary" type="submit" disabled={submitting}>{submitting ? 'Submitting...' : 'Register & Continue'}</button>
        {error && <div style={{color:'red'}}>{error}</div>}
      </form>
    </section>
  );
};

export default Registration;
