import React, { useState } from 'react';
import Banner from './components/Banner';
import Countdown from './components/Countdown';
import Navigation from './components/Navigation';
import Welcome from './components/Welcome';
import Registration from './components/Registration';
import Delegate from './components/Delegate';
import { AdminLogin, AdminParticipants } from './AdminParticipants';
import './App.css';

function App() {
  const [page, setPage] = useState('welcome');
  const [delegateEnabled, setDelegateEnabled] = useState(false);
  const [admin, setAdmin] = useState(false);

  const handleRegistered = () => {
    setDelegateEnabled(true);
    setPage('delegate');
  };

  const handleAdmin = () => setPage('admin-login');

  return (
    <div>
      <Banner />
      <div className="countdown-container"><Countdown /></div>
      <Navigation onNav={setPage} delegateEnabled={delegateEnabled} />
      {page === 'welcome' && <Welcome onRegister={() => setPage('registration')} onAdmin={handleAdmin} />}
      {page === 'registration' && <Registration onRegistered={handleRegistered} />}
      {page === 'delegate' && <Delegate />}
      {page === 'admin-login' && <AdminLogin onSuccess={() => { setAdmin(true); setPage('admin'); }} />}
      {page === 'admin' && admin && <AdminParticipants />}
    </div>
  );
}

export default App;
