import { useEffect, useState } from 'react';
const flierUrls = [
  process.env.PUBLIC_URL + '/flier1.jpg',
  process.env.PUBLIC_URL + '/flier2.jpg',
  process.env.PUBLIC_URL + '/flier3.jpg', // Ensure this file exists
];

const Welcome = ({ onRegister, onAdmin }) => {
  const [slide, setSlide] = useState(0);
  const [prevSlide, setPrevSlide] = useState(0);
  const [transitioning, setTransitioning] = useState(false);

  // Slideshow logic with right-to-left fade transition
  useEffect(() => {
    const timer = setInterval(() => {
      setPrevSlide(slide);
      setTransitioning(true);
      setTimeout(() => {
        setSlide(s => (s + 1) % flierUrls.length);
        setTransitioning(false);
      }, 700); // transition duration
    }, 15000); // 15 seconds
    return () => clearInterval(timer);
  }, [slide, flierUrls.length]);

  return (
    <section className="page active" style={{position:'relative', overflow:'hidden', minHeight: '100vh', minWidth: '100vw', padding:0, display:'flex'}}>
      <div style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        overflow: 'hidden',
        background: '#001f3f',
        flexShrink: 0,
        zIndex: 0,
      }}>
        {flierUrls.map((url, i) => {
          const isCurrent = i === slide;
          const isPrev = i === prevSlide && transitioning;
          if (!isCurrent && !isPrev) return null;
          return (
            <img
              key={i}
              src={url}
              alt={`Conference Flier ${i+1}`}
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100vw',
                height: '100vh',
                objectFit: 'cover',
                opacity: isCurrent
                  ? transitioning ? 1 : 1
                  : transitioning ? 1 : 0,
                zIndex: isCurrent ? 2 : 1,
                transform: transitioning
                  ? (isCurrent
                      ? 'translateX(100%)'
                      : 'translateX(0%)')
                  : (isCurrent ? 'translateX(0%)' : 'translateX(-100%)'),
                transition: transitioning
                  ? 'transform 0.7s cubic-bezier(.4,2,.6,1), opacity 0.7s cubic-bezier(.4,2,.6,1)'
                  : 'none',
                filter: 'brightness(0.85) blur(0.5px)',
              }}
            />
          );
        })}
      </div>
    </section>
  );
};

export default Welcome;
