import React, { useEffect, useState } from 'react';

// Placeholder logo and flier URLs (replace with your actual images in public/ or use URLs)
const logoUrl = process.env.PUBLIC_URL + '/logo-mdcan.png';
const flierUrls = [
  process.env.PUBLIC_URL + '/flier1.jpg',
  process.env.PUBLIC_URL + '/flier2.jpg',
];

const events = [
  {
    day: 'Monday 1st Sept 2025',
    items: ['Arrival of NOC... all day', 'NOC ‑ LOC Meeting .. 7 pm']
  },
  {
    day: 'Tuesday 2nd Sept 2025',
    items: ['Courtesy visits.... all day', 'Arrival of delegates... all day', 'Medical outreach... all day', 'Welcome, cocktail... 7 pm']
  },
  {
    day: 'Wednesday 3rd Sept 2025',
    items: ['Registration 7‑8 am', 'First Scientific session 8‑10:30 am', 'Tea Break 10:30‑11 am', 'Second session 11 am‑12 noon', 'Sub‑theme lecture 12‑1:30 pm', 'Lunch 1:30‑2:30 pm', 'NEC 1 3‑5 pm']
  },
  {
    day: 'Thursday 4th Sept 2025',
    items: ['Registration 7‑8 am', 'Third session 8‑9:30 am', 'Tea Break 10‑11 am', 'Opening Ceremony 11 am‑1 pm', 'Theme lecture 11:45 am‑12:30 pm', 'Lunch 1‑2 pm', 'Product Presentation 2‑2:30 pm', 'NEC 2 3‑5 pm']
  },
  {
    day: 'Friday 5th Sept 2025',
    items: ['Sub‑theme lecture 9:30‑10:15 am', 'Tea Break 10:15‑11 am', 'Product Presentation 11‑11:30 am', 'Lunch/Prayers 11:30‑2 pm', 'BDM & Election, Dinner']
  },
  {
    day: 'Saturday 6th Sept 2025',
    items: ['Press Conference', 'Departure!']
  },
];

const Banner = () => {
  const [slide, setSlide] = useState(0);
  const [flier, setFlier] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setSlide(s => (s + 1) % events.length);
    }, 4000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const timer = setInterval(() => {
      setFlier(f => (f + 1) % flierUrls.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="banner" style={{position:'relative', background:'#28a745', color:'#fff', padding:'3.5rem 1rem 2.5rem 1rem', overflow:'hidden', minHeight:340}}>
      {/* Logo */}
      <img src={logoUrl} alt="MDCAN Logo" style={{position:'absolute', top:24, left:24, width:80, height:80, borderRadius:'50%', boxShadow:'0 2px 12px #001f3f55', background:'#fff', objectFit:'contain', border:'3px solid #ffc107'}} />
      {/* Flier slideshow */}
      <img src={flierUrls[flier]} alt="Conference Flier" style={{position:'absolute', top:24, right:24, width:120, height:120, borderRadius:'1rem', boxShadow:'0 2px 16px #001f3f55', objectFit:'cover', border:'3px solid #28a745', background:'#fff'}} />
      <div style={{position:'relative', zIndex:1, textAlign:'center'}}>
        <h1 style={{fontWeight:900, fontSize:'2.7rem', fontFamily:'Montserrat, sans-serif', letterSpacing:1, marginBottom:8, textShadow:'0 2px 12px #001f3f55'}}>Welcome to MDCAN BDM 042‑2025</h1>
        <p style={{fontWeight:700, fontSize:'1.2rem', fontFamily:'Montserrat, sans-serif', marginBottom:24, textShadow:'0 2px 8px #001f3f55'}}><em>Otanisi Psychosis: The mental health implications of Nigeria's socio-economic shifts</em></p>
        {/* Programme of Events Slideshow */}
        <div style={{background:'#fff9', color:'#001f3f', borderRadius:'1.2rem', display:'inline-block', minWidth:260, maxWidth:420, margin:'0 auto', boxShadow:'0 2px 16px #ffc10755', padding:'1.1rem 1.2rem 1.2rem 1.2rem', fontFamily:'Montserrat, sans-serif', fontWeight:700, fontSize:'1.1rem', transition:'all 0.5s'}}>
          <div style={{fontWeight:900, color:'#28a745', fontSize:'1.2rem', marginBottom:6}}>{events[slide].day}</div>
          <ul style={{margin:0, padding:0, listStyle:'disc inside'}}>
            {events[slide].items.map((item, i) => <li key={i} style={{marginBottom:2}}>{item}</li>)}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Banner;
