import React, { useEffect, useRef } from 'react';

const Welcome = ({ onRegister }) => {
  // Canvas animation for dynamic background
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let width = window.innerWidth;
    let height = 400;
    let particles = [];
    const colors = ['#28a745', '#ffc107', '#001f3f', '#ffffff33'];
    canvas.width = width;
    canvas.height = height;

    function createParticles() {
      particles = [];
      for (let i = 0; i < 60; i++) {
        particles.push({
          x: Math.random() * width,
          y: Math.random() * height,
          r: Math.random() * 18 + 8,
          dx: (Math.random() - 0.5) * 1.2,
          dy: (Math.random() - 0.5) * 1.2,
          color: colors[Math.floor(Math.random() * colors.length)]
        });
      }
    }

    function animate() {
      ctx.clearRect(0, 0, width, height);
      for (let p of particles) {
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.r, 0, 2 * Math.PI);
        ctx.fillStyle = p.color;
        ctx.globalAlpha = 0.5;
        ctx.fill();
        ctx.globalAlpha = 1;
        p.x += p.dx;
        p.y += p.dy;
        if (p.x < 0 || p.x > width) p.dx *= -1;
        if (p.y < 0 || p.y > height) p.dy *= -1;
      }
      requestAnimationFrame(animate);
    }

    createParticles();
    animate();
    window.addEventListener('resize', () => {
      width = window.innerWidth;
      canvas.width = width;
      createParticles();
    });
    return () => {
      window.removeEventListener('resize', () => {});
    };
  }, []);

  return (
    <section className="page active" style={{position:'relative', overflow:'hidden', minHeight: 420}}>
      <canvas ref={canvasRef} style={{position:'absolute', top:0, left:0, width:'100%', height:400, zIndex:0}} />
      <div style={{position:'relative', zIndex:1, paddingTop:40, paddingBottom:20, color:'#001f3f'}}>
        <h2 style={{fontSize:'2.2rem', fontWeight:700, textShadow:'0 2px 8px #fff8'}}>Sub‑themes</h2>
        <ul style={{fontSize:'1.1rem', marginBottom:24}}>
          <li>Medical migration syndrome: fortune and misfortunes</li>
          <li>Physician heal thyself: The mismatch between knowledge and practice</li>
        </ul>
        <h2 style={{fontSize:'2rem', fontWeight:700, marginTop:32, textShadow:'0 2px 8px #fff8'}}>Programme of Events</h2>
        <div style={{display:'flex', flexWrap:'wrap', gap:24, justifyContent:'center'}}>
          <div className="event-card">
            <h3>Monday 1st Sept 2025</h3>
            <ul>
              <li>Arrival of NOC... all day</li>
              <li>NOC ‑ LOC Meeting .. 7 pm</li>
            </ul>
          </div>
          <div className="event-card">
            <h3>Tuesday 2nd Sept 2025</h3>
            <ul>
              <li>Courtesy visits.... all day</li>
              <li>Arrival of delegates... all day</li>
              <li>Medical outreach... all day</li>
              <li>Welcome, cocktail... 7 pm</li>
            </ul>
          </div>
          <div className="event-card">
            <h3>Wednesday 3rd Sept 2025</h3>
            <ul>
              <li>Registration 7 ‑ 8 am</li>
              <li>First Scientific session 8 ‑ 10:30 am</li>
              <li>Tea Break 10:30 ‑ 11 am</li>
              <li>Second session 11 am ‑ 12 noon</li>
              <li>Sub‑theme lecture 12 ‑ 1:30 pm</li>
              <li>Lunch 1:30 ‑ 2:30 pm</li>
              <li>NEC 1 3 ‑ 5 pm</li>
            </ul>
          </div>
          <div className="event-card">
            <h3>Thursday 4th Sept 2025</h3>
            <ul>
              <li>Registration 7 ‑ 8 am</li>
              <li>Third session 8 ‑ 9:30 am</li>
              <li>Tea Break 10 ‑ 11 am</li>
              <li>Opening Ceremony 11 am ‑ 1 pm</li>
              <li>Theme lecture 11:45 am ‑ 12:30 pm</li>
              <li>Lunch 1 ‑ 2 pm</li>
              <li>Product Presentation 2 ‑ 2:30 pm</li>
              <li>NEC 2 3 ‑ 5 pm</li>
            </ul>
          </div>
          <div className="event-card">
            <h3>Friday 5th Sept 2025</h3>
            <ul>
              <li>Sub‑theme lecture 9:30 ‑ 10:15 am</li>
              <li>Tea Break 10:15 ‑ 11 am</li>
              <li>Product Presentation 11 ‑ 11:30 am</li>
              <li>Lunch/Prayers 11:30 ‑ 2 pm</li>
              <li>BDM & Election, Dinner</li>
            </ul>
          </div>
          <div className="event-card">
            <h3>Saturday 6th Sept 2025</h3>
            <ul>
              <li>Press Conference</li>
              <li>Departure!</li>
            </ul>
          </div>
        </div>
        <div style={{textAlign:'center', marginTop:36}}>
          <button className="btn btn-success" style={{fontSize:'1.2rem', padding:'0.8rem 2.2rem', boxShadow:'0 2px 12px #28a74555'}} onClick={onRegister}>
            Register Now
          </button>
        </div>
      </div>
      <style>{`
        .event-card {
          background: #fff9;
          border-radius: 1rem;
          box-shadow: 0 2px 16px #001f3f22;
          padding: 1.2rem 1.5rem;
          min-width: 220px;
          max-width: 260px;
          margin-bottom: 1.5rem;
          transition: transform 0.2s, box-shadow 0.2s;
        }
        .event-card:hover {
          transform: translateY(-8px) scale(1.04);
          box-shadow: 0 8px 32px #28a74533;
        }
        @media (max-width: 900px) {
          .event-card { min-width: 180px; max-width: 98vw; }
        }
      `}</style>
    </section>
  );
};

export default Welcome;
